class Solution {
public:
    void dfs(vector<int>gr[],vector<vector<int>>&dp,int i,vector<int>&vis,vector<int>&cyc,string&col,int&f)
    {
        vis[i]=1;
        cyc[i]=1;

        for(int j=0;j<gr[i].size();j++)
        {
            if(!vis[gr[i][j]])
            {
               dfs(gr,dp,gr[i][j],vis,cyc,col,f);
                if(f)
                return;
            }
            else if(cyc[gr[i][j]])
            {
                f=1;
                return;
            }
			//Upadting memory for ith node
            for(int k=0;k<26;k++)
                dp[i][k]=max(dp[i][k],dp[gr[i][j]][k]);
        }
        dp[i][col[i]-'a']++;
        cyc[i]=0;
        return;
    }
    int largestPathValue(string col, vector<vector<int>>& ed) {
        int n=col.length();
        int m=ed.size();
        vector<int>gr[n];
        //creating graph
        for(int i=0;i<m;i++)
            gr[ed[i][0]].push_back(ed[i][1]);

        vector<vector<int>>dp(n,vector<int>(26));

        vector<int>vis(n);
        vector<int>cyc(n);
        //flag to indicate if cycle is found or not
        int f=0;
        //calling dsf on each unvisted node
        for(int i=0;i<n;i++)
        {
            if(!vis[i])
            dfs(gr,dp,i,vis,cyc,col,f);
            if(f)
            return -1;
        }  

        int ans=0;
        //find answere
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<26;j++)
                ans=max(ans,dp[i][j]);
        }
        return ans;
    }
};
