class Solution
{

    int max;

    boolean hasCycle;

    HashMap<Integer, HashMap<Character, Integer>> dp;


    public int largestPathValue(String colors, int[][] edges) 
    {

        ArrayList<ArrayList<Integer>> graph = new ArrayList<>();

        int n = colors.length();

        for(int i = 0; i < n; i++)
        {
            graph.add(new ArrayList<>());
        }

        for(int ar[] : edges)
        {
            int a = ar[0];

            int b = ar[1];

            graph.get(a).add(b);
        }

        boolean vis[] = new boolean[n];

        max = -1;

        hasCycle = false;

        dp = new HashMap<>();

        for(int i = 0; i < n; i++)
        {
            dfs(i, graph, colors, vis);

            if(hasCycle)
            {
                return -1;
            }
        }


        return max;


    }



    public HashMap<Character, Integer> dfs(int curr, ArrayList<ArrayList<Integer>> graph, String colors, boolean vis[])
    {
       if(vis[curr])
       {
           hasCycle = true;

           return new HashMap<>();
       }

       if(dp.get(curr) != null)
       {
           return dp.get(curr);
       }

        vis[curr] = true;

        ArrayList<Integer> neb = graph.get(curr);

        HashMap<Character, Integer> currMap = new HashMap<>();

        if(neb != null && !neb.isEmpty())
        {
            for(int i : neb)
            {
                HashMap<Character, Integer> resMap = dfs(i, graph, colors, vis);

                if(hasCycle)
                {
                    return currMap;
                }

                for(char c : resMap.keySet())
                {
                    int val = resMap.get(c);

                    int currVal = currMap.getOrDefault(c, 0);

                    currVal = Math.max(currVal, val);

                    currMap.put(c, currVal);

                }
            }
        }


        int currentNodeColorCount = currMap.getOrDefault(colors.charAt(curr), 0);

        currMap.put(colors.charAt(curr), currentNodeColorCount + 1);

        max = Math.max(currentNodeColorCount + 1, max);

        vis[curr] = false;

        dp.put(curr, currMap);

        return currMap;



    }


}

