class Solution {
    private $cache = [];
    /**
     * @param String $colors
     * @param Integer[][] $edges
     * @return Integer
     */
    function largestPathValue($colors, $edges) {
        $map = [];
        foreach($edges as [$a, $b]){
           $map[$a][] = $b;
        }
        $max = 0;
        $visited = array_fill(0, strlen($colors), false);
        for($i = 0; $i<strlen($colors); $i++){
            [$err, $c] = $this->getMaxC($map, $colors, $i, $visited);
            if(!$err){
                $max = max($max, max($c));
            }
            else return -1;
        }

        return $max;
    }

    function getMaxC($map, $colors, $i, &$visited) {
        if($visited[$i]) return [true, []];
        if(isset($this->cache[$i]))
            return [false, $this->cache[$i]];
        $res = $this->def();

        $visited[$i] = true;
        
        foreach($map[$i] as $next) {
            [$err, $subres] = $this->getMaxC($map, $colors, $next, $visited);
            if($err) return [true, []];
            foreach($subres as $c=>$v) {
                $res[$c] = max($res[$c], $v);
            }
        }

        $visited[$i] = false;
        $res[$colors[$i]]++;

        return [false, $this->cache[$i] = $res];
    }

    function def() {
        static $def = null;
        if($def === null) {
            $def = array_combine(range('a', 'z'), array_fill(0, 26, 0));
        }

        return $def;
    }
}
