class Solution {
    public int largestPathValue(String colors, int[][] edges) {
      int n = colors.length();
      HashMap<Integer,List<Integer>> graph = new HashMap<>();
      int[] inDegree = new int[n];
      Queue<Integer> q = new LinkedList<>();
      int[][] count = new int[n][26];
      for(int[] e : edges){
        graph.computeIfAbsent(e[0],k -> new ArrayList<>()).add(e[1]);
        inDegree[e[1]]++;
      }
      for(int i=0;i<n;i++){
        if(inDegree[i] == 0){
          q.offer(i);
        }
      }
      for(int i=0;i<n;i++){
        count[i][colors.charAt(i) - 'a']++;
      }
      int visited = 0;
      int res = 0;
      while(!q.isEmpty()){
        int curr = q.poll();
        visited++;
        if(graph.containsKey(curr)){
        for(int child : graph.get(curr)){
          for(int i=0;i<26;i++){
              count[child][i] = Math.max(count[child][i] , count[curr][i] + (colors.charAt(child) - 'a' == i ? 1 : 0));
          }
          inDegree[child]--;
          if(inDegree[child] == 0){
              q.add(child);
          }
        }
        }
        res = Math.max(res,Arrays.stream(count[curr]).max().getAsInt());
      }
      return visited == n ? res : -1;
    }
}
