class Solution {
public:
    int largestPathValue(string c, vector<vector<int>>& e) {
        int n = c.size();
        vector<vector<int>> g(n);
        vector<vector<int>> cd(n,vector<int> (26,0));
        vector<int> in(n,0);


        for(auto &i: e)
        {
            g[i[0]].push_back(i[1]);
            in[i[1]]++;
        }

        queue<int> q;
        for(int  i = 0;i<n;i++)
        {
            if(in[i]==0)
            {
                q.push(i);
                cd[i][c[i]-'a'] = 1;
            }

        }

        int ans =0 , cnt = 0;
        while(!q.empty())
        {
            int node  = q.front();
            q.pop();
            int val  = *max_element(cd[node].begin(),cd[node].end());
            ans = max(ans,val);
            cnt++;
            for(int v: g[node])
