                for(int i=0;i<26;i++)
                {
                    cd[v][i] = max(cd[v][i],cd[node][i]+(i==(c[v]-'a')));
                }
                in[v]--;
                if(in[v] == 0)
                    q.push(v);

            }
        }

        return cnt==n ?ans:-1;

    }
