        while(!q.empty())
        {
            int node  = q.front();
            q.pop();
            int val  = *max_element(cd[node].begin(),cd[node].end());
            ans = max(ans,val);
            cnt++;
            for(int v: g[node])
            {
                for(int i=0;i<26;i++)
                {
                    cd[v][i] = max(cd[v][i],cd[node][i]+(i==(c[v]-'a')));
                }
                in[v]--;
                if(in[v] == 0)
                    q.push(v);

            }
        }

        return cnt==n ?ans:-1;

    }
};
