class Solution {
public:
    int largestPathValue(string colors, vector<vector<int>>& edges) {
        
        int n = colors.size();
        vector<vector<int>> graph(n);

        for(auto &i : edges)
            graph[i[0]].push_back(i[1]);

        vector<bool> visited(n, false), visited2 = visited;
        for(int i = 0; i < n; i++)
            if(!visited[i] && detectCycle(graph, visited, visited2, i))
                return -1;

        int mx = 0;
        for(int i = 0; i < n; i++)
            visited[i] = false;

        unordered_map<int, vector<int>> mp;
        for(int i = 0; i < n; i++)
            if(mp.find(i) == mp.end())
            {
                vector<int> dp = helper(graph, colors, visited, i, mp);
                for(auto &i : dp)
                    mx = max(mx, i);
            }

        return mx;
    }

    bool detectCycle(vector<vector<int>> &graph, vector<bool> &visited, vector<bool> &visited2, int i)
    {
        if(visited[i])
            return visited2[i];

        visited[i] = visited2[i] = true;
        for(auto &j : graph[i])
            if(detectCycle(graph, visited, visited2, j))
                return true;

        return visited2[i] = false;
    }

    vector<int> helper(vector<vector<int>> &graph, string &colors, vector<bool> &visited, int i, unordered_map<int, vector<int>> &mp)
    {
        vector<int> dp(26, 0);

        if(mp.find(i) != mp.end())
            return mp[i];

        for(auto &j : graph[i])
        {
            auto tmp = helper(graph, colors, visited, j, mp);
            for(int i = 0; i < 26; i++)
                dp[i] = max(tmp[i], dp[i]);
        }

        dp[colors[i] - 'a']++;
        return mp[i] = dp;
    }
};
