public class Solution {
    public int LargestPathValue(string colors, int[][] edges) {
        int n = colors.Length;
        var adj = new Dictionary<int, List<int>>();
        int[] indegree = new int[n];

        foreach (int[] edge in edges) {
            if (!adj.ContainsKey(edge[0])) {
                adj[edge[0]] = new List<int>();
            }
            adj[edge[0]].Add(edge[1]);
            indegree[edge[1]]++;
        }

        int[][] count = new int[n][];
        for (int i = 0; i < count.Length; ++i) {
            count[i] = new int[26];
        }
        var q = new Queue<int>();

        // Push all the nodes with indegree zero in the queue.
        for (int i = 0; i < n; i++) {
            if (indegree[i] == 0) {
                q.Enqueue(i);
            }
        }

        int answer = 1, nodesSeen = 0;
        while (q.Count > 0) {
            int node = q.Dequeue();
            answer = Math.Max(answer, ++count[node][colors[node] - 'a']);
            nodesSeen++;

            if (!adj.ContainsKey(node)) {
                continue;
            }

            foreach (int neighbor in adj[node]) {
                for (int i = 0; i < 26; i++) {
                    // Try to update the frequency of colors for the neighbor to include paths
                    // that use node->neighbor edge.
                    count[neighbor][i] = Math.Max(count[neighbor][i], count[node][i]);
                }

                indegree[neighbor]--;
                if (indegree[neighbor] == 0) {
                    q.Enqueue(neighbor);
                }
            }
        }

        return nodesSeen < n ? -1 : answer;        
    }
}
