class Solution {
    Stack<Integer> stk = new Stack<>();
    ArrayList<ArrayList<Integer>> dp = new ArrayList<>(); //for node at i'th it stores list of size 26 storing frequency of 26 colors.
    ArrayList<ArrayList<Integer>> graph = new ArrayList<>();

    boolean[] visited;
    boolean[] dfsVisited;

    int ans=0;
    boolean cycle=false;
    public int largestPathValue(String colors, int[][] edges) {
        graph = build(colors.length(),edges);//Build graph;
        visited = new boolean[colors.length()];
        Arrays.fill(visited,false);
        for(int i=0;i<colors.length();i++){
            dp.add(new ArrayList<>());
        }
        dfsVisited = new boolean[colors.length()];
        Arrays.fill(dfsVisited,false);
        for(int i=0;i<colors.length();i++){
            dfs(i);
        }
        if(cycle) return -1;
        while(!stk.isEmpty()){
            ArrayList<Integer> arr = recurr(stk.pop(),colors);

            for(int num: arr){
                ans = Math.max(ans,num);
            }
        }
        return ans;
    }
    public ArrayList<Integer> recurr(int u, String str){

        if(dp.get(u).size()==26) return dp.get(u);
        ArrayList<Integer> p = new ArrayList<>();
        for(int i=0;i<26;i++) p.add(0);

        for(int it: graph.get(u)){
            ArrayList<Integer> adj = recurr(it,str);
            for(int i=0;i<26;i++) p.set(i,Math.max(p.get(i),adj.get(i)));
        }
        p.set(str.charAt(u)-'a',p.get(str.charAt(u)-'a')+1);
        dp.set(u,p);
        return p;
    }
    public ArrayList<ArrayList<Integer>> build(int u, int[][] edges){
        for(int i=0;i<=u;i++) graph.add(new ArrayList<>());
        for(int[] edge: edges) graph.get(edge[0]).add(edge[1]);
        return graph;
    }
    public void dfs(int u){
        if(visited[u]==true) return;

        visited[u]=true;
        dfsVisited[u]=true;
        for(int it: graph.get(u)){
            if(dfsVisited[it]) {
                cycle=true;
                return;
            }
            dfs(it);
        }
        stk.add(u);
        dfsVisited[u]=false;
    }
}
