class Solution {
    //dfs visited->n
    //visited->n*26
    public boolean find(List<List<Integer>>graph,int i,boolean[]dfs,int[][]visit,String colors){
        dfs[i]=true;
        visit[i][0]=0;  //i already visited it


        for(int child:graph.get(i)){
            if(dfs[child])return false;

            if(visit[child][0]==-1)
                if(!find(graph,child,dfs,visit,colors))return false;

            for(int j=0;j<26;j++)
                visit[i][j]=Math.max(visit[i][j],visit[child][j]);

        }
        visit[i][colors.charAt(i)-'a']++;
        dfs[i]=false;
        return true;
    }
    public int largestPathValue(String colors, int[][] edges) {
        int n=colors.length();
        int maxf=0;
        int[][]visit=new int[n][26];
        List<List<Integer>>graph=new ArrayList<>();

        for(int i=0;i<n;i++)
            graph.add(new ArrayList<>());

        for(int i=0;i<edges.length;i++)
            graph.get(edges[i][0]).add(edges[i][1]);


        for(int i=0;i<n;i++)
            visit[i][0]=-1;

        for(int i=0;i<n;i++){
            if(visit[i][0]==-1){
                if(!find(graph,i,new boolean[n],visit,colors))return -1;

                for(int j=0;j<26;j++)
                    maxf=Math.max(maxf,visit[i][j]);
            }
        }
        return maxf;
    }
}
