    public int largestPathValue(String colors, int[][] edges) {
        //dp[i][j] : largest color value of ending at node i with colour j 
        int n = colors.length() ;
        Map<Integer , ArrayList<Integer>> map = new HashMap<>() ;
        for(int i=0 ; i<n ; i++){
            map.put(i,new ArrayList<>()) ;
        }
        int indeg[] = new int[n] ;
         for(int ar[] : edges){
             indeg[ar[1]]++ ;
             map.get(ar[0]).add(ar[1]) ;
        }

        Queue<Integer> qu = new LinkedList<>() ;
        for(int i=0 ; i<n ; i++){
            if(indeg[i]==0)
                qu.add(i) ;
        }

        int dp[][] = new int[n][26] ;
        int vis=0 ;

        while(!qu.isEmpty()){
            int node = qu.poll() ;
            int color = colors.charAt(node)-'a' ;
            dp[node][color]++ ;
            for(int nei : map.get(node)){

                for(int i=0 ; i<26 ; i++)
				//using current node as parent to update child nodes value
				//think of the case where two nodes having indegree 0 are pointing to the same child node
                    dp[nei][i] = Math.max(dp[nei][i] , dp[node][i]) ;

                if(--indeg[nei]==0)
                    qu.add(nei) ;
