class Solution {
    public int largestPathValue(String colors, int[][] edges) {

        //NOT ORIGINAL JUST EXPLAINING ONE GOOD METHOD IN DISCUSS BOARD

        //consider using DFS + dp, record each node with 26(letters) of it's best records.
        //build adjacent list & color hashMap at start
        //step1, build the graph with a adjacent list manner O(n)
        //step2, do DFS search through each nodes, on each node:
        //if visited, looped, return -1
        //label as visited
        //Do dfs to child nodes
        //add the color count to color-count map, if it's bigger than ansMax, update ansMax
        //complexity O(n+m)

        HashMap<Integer, ArrayList<Integer>> adjList = new HashMap<>();//adjacent list representation of a graph
        for(int[] e : edges){//building adjacent list
            if(!adjList.containsKey(e[0])){
                ArrayList<Integer> li = new ArrayList<>();
                li.add(e[1]);
                adjList.put(e[0],li);
            }
            else{
                ArrayList<Integer> li = adjList.get(e[0]);
                li.add(e[1]);
            }
        }
        int nodeNum = colors.length();

        int ans = 0;//ansMax
        int[][] maxRecord = new int[nodeNum][26]; // max record for each node, each color
        int[] visited = new int[nodeNum];// visited list for graph, 0:not visited, 1:visited
        for(int i = 0; i < nodeNum; i++){
            if(visited[i]!=0)continue;
            if(!dfs(i, visited, maxRecord, adjList, colors))return -1;
            //maxRecord is updated during dfs()
            for(int max : maxRecord[i])ans = Math.max(max, ans);//try to update answer
        }
        return ans;
    }
    private boolean dfs(int curNode, int[] visited, int[][] maxRecord, 
                        HashMap<Integer,ArrayList<Integer>> adjList, String colors){
        if(visited[curNode]==1)return true;//verified before
        if(visited[curNode]==2)return false;//specifically for this node involved self-cycle, set back to 1 later
        visited[curNode]=2;
        if(adjList.get(curNode)==null){
            maxRecord[curNode][colors.charAt(curNode)-'a'] += 1;//add self to the color
            visited[curNode] = 1;//label verified
            return true;
        }
        for(int neighbor : adjList.get(curNode)){
            if(!dfs(neighbor, visited, maxRecord, adjList, colors))return false;//my child goes a loop
            for(int i = 0; i < maxRecord[curNode].length; i ++){
                maxRecord[curNode][i] = Math.max(maxRecord[curNode][i],maxRecord[neighbor][i]);//adopt neighbor-subtree
            }
        }
        maxRecord[curNode][colors.charAt(curNode)-'a'] += 1;//add self to the color
        visited[curNode] = 1;//label verified
        return true;
    }
}
