from collections import defaultdict, deque

class Solution:
    def largestPathValue(self, colors: str, edges: List[List[int]]) -> int:
        n = len(colors)

        # Map the colors from str to int
        color_map = {}
        for c in colors:
            if c not in color_map:
                color_map[c] = len(color_map)
        color_size = len(color_map)

        # Construct the graph with adj list and calc the in degree of each node
        neighbor = defaultdict(lambda: [])
        deg = [0 for i in range(n)]
        for a, b in edges:
            neighbor[a].append(b)
            deg[b] += 1

        # Topological Sorting and Dynamic Programming
        queue = deque()

        # Each node has a list. The ith element represents
        # the maximum number of nodes with ith color in the paths that 
        # has been found so far.
        dp = defaultdict(lambda: [0 for i in range(color_size)])

        for i in range(n):
            if deg[i] == 0:
                queue.append(i)
                c = colors[i]
                dp[i][color_map[c]] = 1

        maxLen = 1
        while len(queue) > 0:
            node = queue.popleft()

            for adj in neighbor[node]:
                deg[adj] -= 1
                cid = color_map[colors[adj]]

                for j in range(color_size):
                    offset = 1 if cid == j else 0
                    dp[adj][j] = max(dp[adj][j], dp[node][j] + offset)
                    maxLen = max(dp[adj][j], maxLen)
                if deg[adj] == 0:
                    queue.append(adj)

        if sum(deg) > 0:
            return -1
        else:
            return maxLen
