class Solution {
public:
    int res;
    unordered_map<char, int> mp;
    int dfs(vector<int > gf[] , int x, vector<int> &vis, vector<int> &stk){
        if(vis[x]==1)return 1;
        if(vis[x]==2)return 0;
        vis[x]=2;
        for(auto it:gf[x]){

            if(!dfs(gf,it,vis,stk))return 0;
        }
        vis[x]=1;
        stk.push_back(x);
        return 1;
    }

    int dfs2(vector<int > gf[] , int x,vector<vector<int>> &dp,string &col, int cur_col ){

        if(dp[x][cur_col]!=-1)return dp[x][cur_col];

        dp[x][cur_col]=0;
        int mx=0;

        for(auto it:gf[x]){
            mx=max(mx,dfs2(gf,it,dp,col,cur_col));
        }

        dp[x][cur_col]=mx;
        if(mp.find(col[x])!=mp.end()&&(cur_col == mp[col[x]])){
          dp[x][cur_col]++;  
        }
        return dp[x][cur_col];
    }

    int largestPathValue(string col, vector<vector<int>>& e) {
        int n=col.length();
        vector<int> gf[n];
        for(auto it:e){
            gf[it[0]].push_back(it[1]);
        }

        /// find topological order->>>
        vector<int>vis(n,0);
        vector<int>stk;
        for(int i=0;i<n;i++){ 
            if(!vis[i]) 
            if(!dfs(gf,i,vis,stk))return -1;
        }
        reverse(stk.begin(),stk.end());

        // found topologincal order 

        // in topological order assume each colour is ans so check for each , only 26 so we would be fine O(n*26)
        set<char> st(col.begin(),col.end());

        vector<vector<int>> dp(n,vector<int> ( st.size(), -1));

        int iii=0;
        for(auto it:st)mp[it]=iii++;

        for(auto it:stk){
            for(auto i : st){
                res=max(res,dfs2(gf,it,dp,col,mp[i]));
            }
        }

        return res;
    }
};
