class Solution {
public:
    int largestPathValue(string colors, vector<vector<int>>& edges) 
    {   
        int sz = colors.size();
        queue<int> q;
        vector<int> indegree(sz,0);
        vector<vector<int>> adj(sz);
        vector<vector<int>> charCount(sz, vector<int>(26,0));

        for(auto e : edges)
        {
            adj[e[0]].push_back(e[1]);
            indegree[e[1]]++ ;
        }
        for(int i=0; i<sz; i++)
        {
            if(indegree[i] == 0){q.push(i);}
        }
        int res = 0 ; int visited = 0;

        while(!q.empty())
        {
            int node = q.front(); q.pop(); visited++ ;
            res = max(res, ++charCount[node][colors[node] - 'a']);
            for(auto it : adj[node])
            {
                indegree[it]-- ;
                if(indegree[it] == 0){q.push(it);}
                for(int c=0; c<26; c++)
                {
                    charCount[it][c] = max(charCount[node][c], charCount[it][c]) ;
                }
            }
        }
        if(visited != sz){return -1;}

        return res;
    }
};
