/**
 * @param {string} colors
 * @param {number[][]} edges
 * @return {number}
 */
var largestPathValue = function(colors, edges) {
    /*Here we are populating a dictionary 'data', where data[node] will have max possible count for each color(the colors which are available in paths starting from 'node') in any of the path starting from 'node'
    Now lets consider this example 
         --->1(c)->2(b)->3(a)
       / 
0(a) /
    |\
    |  \ ---> 4(a)->5(a)->6(b)
    |
	|
    |--->7(b)->8(c)->9(c)

    for the above graph
    data[3]=>{a:1}
    data[2]=>{a:1,b:1}
    data[1]=>{a:1,b:1,c:1}

    data[6]=>{b:1}
    data[5]=>{a:1,b:1}
    data[4]=>{a:2,b:1}

    data[9]=>{c:1}
    data[8]=>{c:2}
    data[7]=>{b:1,c:2}

    data[0]=>{a:3,b:1,c:2}

    Whenver we call dfs with any 'node' then the output data[node] will have the maximum possible values for all the colors.
    */
    let data={},maxColorCount=-1,maxColor=-1;
    let visited = {},colorCountMap={},edgesMap={},max=-1,res;
    for(let i=0;i<edges.length;i++){
        if(edgesMap[edges[i][0]]===undefined){
            edgesMap[edges[i][0]]=[];
        }
        edgesMap[edges[i][0]].push(edges[i][1]);
    }
    for(let i=0;i<colors.length;i++){     
        res = dfs(i);
        if(res===false){
            return -1;
        }else{
            for(const[key,val] of Object.entries(res)){
                if(val>maxColorCount){
                    maxColorCount=val;
                    maxColor=key;
                }
            }
        }
    }

    return maxColorCount;

    function dfs(node){
        let res=false,map=false;

        if(data[node]!==undefined){//We have already visited all the paths from this node
            return data[node];
        }
        if(visited[node]!==undefined){//This is a cycle, we got this node again while visiting of its paths
            return false;
        }
        visited[node]=true;
        if(edgesMap[node]!==undefined){
            for(let i=0;i<edgesMap[node].length;i++){
                res = dfs(edgesMap[node][i]);
                if(res===false){
                    return false;
                }
                if(map===false){
                    map=JSON.parse(JSON.stringify(res));
                }else{
                    for(const[key,val] of Object.entries(res)){
                        if(map[key]===undefined){
                            map[key]=val;
                        }else{
                            if(map[key]<val){
                                map[key]=val;
                            }
                        }
                    }
                }
            }
        }
        if(map===false){//If this is an edge node, we dont have any outgoing edge from this node
            map={};
            map[colors[node]]=1;//So the path starting from this node will have only one color, the color of node itself
        }else if(map[colors[node]]===undefined){//If none of the paths starting from this node contains the same color as of this node. 
            map[colors[node]]=1;
        }else{//Increment the max count by 1 for the node's color
            map[colors[node]]+=1;
        }
        delete visited[node];
        data[node]=JSON.parse(JSON.stringify(map));
        return data[node];
    }
};
