class Solution {
    int maxColor = 0;
    public int largestPathValue(String colors, int[][] edges) {
        Set<Character> colorSet = new HashSet<>();
        for(int i = 0; i < colors.length(); i++) colorSet.add(colors.charAt(i));
        int maxCol = 0;
        Map<Integer, List<Integer>> graph = new HashMap<>();
        for(int i = 0; i < edges.length; i++) {
            if(graph.containsKey(edges[i][0]) == false) graph.put(edges[i][0], new ArrayList<>());
            graph.get(edges[i][0]).add(edges[i][1]);
        }
        if(checkCycle(graph, colors.length())==true) return -1;
        for (char color : colorSet) {
            maxColor(graph, color, colors);
            maxCol = Math.max(maxCol, maxColor);
        }
        return maxCol;
    }
    private boolean checkCycle(Map<Integer, List<Integer>> graph, int nodes) {
        int[] states = new int[nodes];
        for(int i = 0; i < nodes; i++) if(states[i] == 0 && checkCycleDfs(graph, i, states) == true) return true;
        return false;
    }
    private boolean checkCycleDfs(Map<Integer, List<Integer>> graph, int node, int[] states) {
        if(states[node] == 1) return true;
        if(states[node] == 2) return false;
        states[node] = 1;
        List<Integer> nodes = graph.getOrDefault(node, new ArrayList<Integer>());
        for(int i = 0; i < nodes.size(); i++) if(checkCycleDfs(graph, nodes.get(i), states) == true) return true;
        states[node] = 2;
        return false;
    }
    private void maxColor(Map<Integer, List<Integer>> graph, char color, String colors) {
        int[] maxCols = new int[colors.length()];
        maxColor = 0;
        for(int i = 0; i < colors.length(); i++) maxCols[i] = -1;
        for(int i = 0; i < colors.length(); i++) if(maxCols[i] == -1) maxColor = Math.max(maxColor, maxColorDfs(graph, i, maxCols, color, colors));
    }
    private int maxColorDfs(Map<Integer, List<Integer>> graph, int node, int[] maxCols, char color, String colors) {
        if(maxCols[node] > -1) return maxCols[node];
        if(colors.charAt(node) == color) maxCols[node] = 1;
        else maxCols[node] = 0;
        int otherCost = 0;
        List<Integer> nodes = graph.getOrDefault(node, new ArrayList<Integer>());
        for(int i = 0; i < nodes.size(); i++) otherCost = Math.max(otherCost, maxColorDfs(graph, nodes.get(i), maxCols, color, colors));
        maxCols[node] += otherCost;
        return maxCols[node];
    }
}
