class Solution:
    def getColors(self, color1, color2):
        color = [0 for i in range(26)]
        for i in range(26):
            color[i] = max(color1[i], color2[i])
        return color

    def addColor(self, dp, colors, vertex):
        new_color = dp.copy()
        new_color[ord(colors[vertex]) - 97] += 1

        return new_color

    def getTopSort(self, in_degree, adjList):
        counted_so_far, bfs = 0, []
        self.topSort = []
        for i, degree in enumerate(in_degree):
            if not degree:
                bfs.append(i)

        while bfs:
            node = bfs.pop(-1)
            self.topSort.append(node)
            counted_so_far += 1

            if node in adjList:
                for vertex in adjList[node]:
                    in_degree[vertex] -= 1
                    if not in_degree[vertex]:
                        bfs.append(vertex)

        return counted_so_far == len(in_degree)

    def largestPathValue(self, colors: str, edges: List[List[int]]) -> int:
        adjList = dict()
        in_degree = [0 for i in range(len(colors))]

        for src, dest in edges:
            if src not in adjList:
                adjList[src] = []
            adjList[src].append(dest)
            in_degree[dest] += 1

        if not self.getTopSort(in_degree, adjList):
            return -1

        ans = 1
        dp = [[0 for i in range(26)] for i in range(len(colors))]
        for i in self.topSort:
            dp[i][ord(colors[i]) - 97] = 1

        for node in self.topSort:
            if node in adjList:
                for vertex in adjList[node]:
                    dp[vertex] = self.getColors(self.addColor(dp[node], colors, vertex), dp[vertex])
                    ans = max(ans, max(dp[vertex]))
        return ans


