class Solution:
    def largestPathValue(self, colors: str, edges: List[List[int]]) -> int:
        res, seen, n = 0, 0, len(colors)
        graph, q = defaultdict(list), deque()
        in_degree = [0] * n
        count = [defaultdict(int) for _ in range(n)]
        for u, v in edges:
            graph[v].append(u)
            in_degree[u] += 1
        for node in range(n):
            if not in_degree[node]:
                q.append(node)
        while q:
            cur = q.popleft()
            count[cur][colors[cur]] += 1
            res = max(res, count[cur][colors[cur]])
            seen += 1
            for v in graph[cur]:
                for c in count[cur]:
                    count[v][c] = max(count[v][c], count[cur][c])
                in_degree[v] -= 1
                if not in_degree[v]:
                    q.append(v)
        if seen < n:
            return -1
        return res

