class Solution {
    public int largestPathValue(String colors, int[][] edges) {
        HashMap<Integer, List<Integer>> adjMap = new HashMap<>();
        int n = colors.length();
        int[] indegree = new int[n];
        int[][] paths = new int[n][26];

        for (int[] edge : edges) {
            List<Integer> adj = new ArrayList<>(adjMap.getOrDefault(edge[0], new ArrayList<>()));
            adj.add(edge[1]);
            adjMap.put(edge[0], new ArrayList<>(adj));
            indegree[edge[1]]++;
        }
        
        Queue<Integer> q = new LinkedList<>();
        for (int i = 0; i < indegree.length; i++) {
            if (indegree[i] == 0) {
                q.add(i);
                paths[i][colors.charAt(i) - 'a']++;
            }
        }
        
        if (q.size() == 0) return -1;
        
        while (q.size() != 0) {
            int qSize = q.size();
            for (int i = 1; i <= qSize; i++) {
                Integer node = q.poll();
                List<Integer> adj = new ArrayList<>(adjMap.getOrDefault(node, new ArrayList<>()));
                for (int j = 0; j < adj.size(); j++) {
                    int adjNode = adj.get(j);
                    for (int k = 0; k < 26; k++) {
                        paths[adjNode][k] = Math.max(paths[adjNode][k],
                         paths[node][k] + (k == colors.charAt(adjNode) - 'a' ? 1 : 0));
                    }
                    indegree[adjNode]--;
                    if (indegree[adjNode] == 0) {
                        q.add(adjNode);
                    }
                }
            }
        }
        if(cycleExist(indegree))return -1;
        int res = findMaxOccurringPathColor(paths, n);
        return res == 0 || q.size()!=0? -1 : res;
    }
    private boolean cycleExist(int[] indegree){
        for(int i=0; i<indegree.length; i++){
            if(indegree[i]!=0)return true;
        }
        return false;
    }    
    private int findMaxOccurringPathColor(int[][] paths, int n) {
        int max = 0;
        for (int i = 0; i < n; i++) {
            int[] path = paths[i];
            for (int j = 0; j < 26; j++) {
                max = Math.max(max, path[j]);
            }
        }
        return max;
    }
}

