from collections import Counter, defaultdict

NEW = 0
ACTIVE = 1
DONE = 2


class Solution:
    def largestPathValue(self, colors: str, edges: list[list[int]]) -> int:
        n = len(colors)
        g = self.create_graph(edges)

        color_cnt = [Counter() for _ in range(n)]
        status = [NEW] * n

        def got_cycle(u: int) -> bool:
            if status[u] == DONE:
                return False

            status[u] = ACTIVE
            cnt = color_cnt[u]

            for v in g[u]:
                if status[v] == NEW:
                    if got_cycle(v):
                        return True
                elif status[v] == ACTIVE:
                    return True

                cnt |= color_cnt[v]

            cnt[colors[u]] += 1
            status[u] = DONE

            return False

        if any(map(got_cycle, range(n))):
            return -1
        else:
            return max(map(self.best_color_value, color_cnt))

    @staticmethod
    def best_color_value(cnt: Counter[str]) -> int:
        return max(cnt.values())

    @staticmethod
    def create_graph(edges: list[list[int]]) -> defaultdict[int, list[int]]:
        g = defaultdict(list)

        for u, v in edges:
            g[v].append(u)

        return g
