# Intuition
This is one typical visiting graph problem. Two strategies: deep-first or breadth-first. Here the deep-first is used. 

# Approach

## Clean the graph
We notice that the graph need be cleaned before starting searching. The path should be shortened to one node if:
- The out degree of start node is 1
- The in degree of end node is 1

## Visit the graph using deep-first
To avoid repeating computation, the data structure `maxColorMapArray` is used and it is returned directly if this node is visisted already.

### How to design the data structure?
It should be one array to store the map for each output node. The element is the dictionary map, where the key is the color and the value is the frequence.

# Complexity
- Time complexity:
$$O(N+E)$$
Where N is the number of node and E is the number of edge.

- Space complexity:
$$O(N)$$


# Code
```js
const VisistingState = "Visiting";
const UnvisistedState = "Unvisiting";
const VisitedState = "Visited";

const LoopState = -1;

/**
 * @param {string} colors
 * @param {number[][]} edges
 * @return {number}
 */
var largestPathValue = function(colors, edges) {
    if (edges.length < 1) {
        return 1;
    }
    // firstly we need to build a graph

    const colorList = [...colors];
    let elements = colorList.map((color, index) => new Element(index, color));
    edges.forEach(edge => {
        const [from, to] = edge;
        elements[from].addNext(elements[to]);
        elements[to].inDegree++;
        elements[from].outDegree++;
    });
    // clean up the graph
    const loop = cleanGraph(elements) == -1;
    if (loop) {
        return LoopState;
    }
    elements = elements.filter(ele => !ele.toBeRemoved);
    // find the longest path
    elements.forEach(ele => ele.unsetVisited());
    const maxPathCount = findLongestPath(elements);

    return maxPathCount;
};

/**
 * Find the longest path
 * @param {Element[]} elements 
 */
const findLongestPath = function(elements) {
    const heads = elements.filter(ele => ele.inDegree === 0);
    let maxPathCount = -1;
    for (const root of heads) {
        const {maxColorMapArray, maxPathCount: pathCount} = findDepthFrist(root);
        if (pathCount === LoopState) {
            return LoopState;
        }
        maxPathCount = Math.max(maxPathCount, pathCount);
    }
    elements.forEach(ele => ele.unsetVisited());
    return maxPathCount;
}

/**
 * find the longest path from root
 * @param {Element} root 
 * @returns {{}} return the max color map array and the max path count
 */
const findDepthFrist = function(root) {
    if (root.isVisiting()) {
        return {maxColorMapArray: undefined, maxPathCount: LoopState};
    }

    if (root.isVisited()) {
        return {maxColorMapArray: root.maxColorMapArray, maxPathCount: root.maxPathCount};
    }

    if (root.nextList.length === 0) {
        // it is a leaf
        root.setVisited();
        root.maxColorMapArray = [root.colorMap];
        root.maxPathCount = getMaxCountFromColorMapArray(root.maxColorMapArray);
        return {maxColorMapArray: root.maxColorMapArray, maxPathCount: root.maxPathCount};
    }

    root.setVisiting();
    let maxColorMapArray = [];

    root.nextList.forEach(next => {
        const {maxColorMapArray: nextColorMapArray, maxPathCount: nextMaxPathCount} = findDepthFrist(next);
        if (nextMaxPathCount === LoopState) {
            return {maxColorMapArray: undefined, maxPathCount: LoopState};
        }

        const maxColorMap = getMaxCountForEachColor(root.colorMap, nextColorMapArray);
        maxColorMapArray.push(maxColorMap);
    });

    root.setVisited();
    root.maxColorMapArray = maxColorMapArray;
    root.maxPathCount = getMaxCountFromColorMapArray(root.maxColorMapArray);
    return {maxColorMapArray: root.maxColorMapArray, maxPathCount: root.maxPathCount};
};

const getMaxCountFromColorMapArray = function(colorMapArray) {
    return Math.max(
        ...Array.from(
            colorMapArray.map(
                colorMap => Math.max(...Array.from(colorMap.values()) )
            )
        )
    );
};

/**
 * Get the max path from colorMapArray
 * @param {Map} colorMap1 
 * @param {Map[]} colorMapArray 
 * @returns {Map} the max color map
 */
const getMaxCountForEachColor = function(colorMap1, colorMapArray) {
    let maxColorMap = new Map(colorMap1);
    colorMapArray.forEach(colorMap => {
        let rawColorMap = new Map(colorMap1);
        updateColorMapFromColorMap(rawColorMap, colorMap);

        rawColorMap.forEach((count, color) => {
            const countNew = maxColorMap.get(color) || 0;
            maxColorMap.set(color, Math.max(count, countNew));
        });
    });
    return maxColorMap;
}

const updateColorMapFromColorMapArray = function(colorMapIn, colorMapArray) {
    colorMapArray.forEach(colorMap => {
        updateColorMapFromColorMap(colorMapIn, colorMap);
    });
};

const updateColorMapFromColorMap = function(colorMapIn, colorMap) {
    colorMap.forEach((count, color) => {
        const countNew = colorMapIn.get(color) || 0;
        colorMapIn.set(color, count + countNew);
    });
};

/**
 * Clean up the graph
 * @param {Element[]} elements
 * @returns 1 if success, -1 if there is a loop
 */
const cleanGraph = function(elements) {
    const unvisitedElements = [...elements];
    let unvisitedEle = unvisitedElements.pop();
    while (unvisitedEle) {
        const {simplifiedEle: simplifiedEle, loop: loop} = simplifyGraph(unvisitedEle);
        if (loop) {
            return LoopState;
        }
        unvisitedEle = unvisitedElements.pop();
    }
    elements = elements.filter(ele => !ele.toBeRemoved);
    elements.forEach(ele => ele.unsetVisited());
    return 1;
}

/**
 * @param {Element} root
 * @returns {{Element, boolean}} return simplified element and whether there is a loop
 */
const simplifyGraph = function(root) {
    if (root.isVisiting()) {
        return {simplifiedEle: root, loop: true};
    }

    if (root.isVisited()) {
        return {simplifiedEle: root, loop: false};
    }

    root.setVisiting();
    let loop = false;

    if (root.isSingleOutDegree() && root.nextList.length === 1) {
        let next = root.nextList[0];
        const {simplifiedEle: simplifiedEleNext, loop: nextLoop} = simplifyGraph(next);
        loop = loop || nextLoop;
        if (!loop && simplifiedEleNext.isSingleInDegree()) {
            simplifiedEleNext.toBeRemoved = true;
            root.nextList = simplifiedEleNext.nextList;
            simplifiedEleNext.colorMap.forEach((count, color) => {
                root.setColor(color, count);
            });
            root.outDegree = simplifiedEleNext.outDegree;
        }
    }
    root.setVisited();
    return {simplifiedEle: root, loop};
};

/**
 * Print the graph
 * @param {Element[]} elements
 */
const printGraph = function(elements) {
    const heads = elements.filter(ele => ele.inDegree === 0);
    heads.forEach(head => printDepthFrist(head));
    elements.forEach(ele => ele.unsetVisited());
}

/**
 * Print the graph depth first
 * @param {Element} root
 */
const printDepthFrist = function(root) {
    if (root.isVisited()) {
        return;
    }
    root.setVisited();
    root.print();
    root.nextList.forEach(next => printDepthFrist(next));
    if (root.nextList.length === 0) {
        console.log("end");
    }
}

class Element {
    constructor(index, color) {
        this.index = index;
        this.colorMap = new Map();
        this.setColor(color);
        this.nextList = [];
        this.state = UnvisistedState;
        this.inDegree = 0;
        this.outDegree = 0;
        this.toBeRemoved = false;
        this.maxPathCount = 0;
        this.maxColorMapArray = [];
    }

    print() {
        console.log(`index: ${this.index}, colorMap: ${this.colorMap}, nextList: ${this.nextList.map(ele => ele.index)}`);
    }

    setColor(color, count = 1) {
        const countNew = this.colorMap.get(color) || 0;
        this.colorMap.set(color, count + countNew);
    }

    addNext(next) {
        this.nextList.push(next);
    }

    setVisiting() {
        this.state = VisistingState;
    }

    setVisited() {
        this.state = VisitedState;
    }

    unsetVisited() {
        this.state = UnvisistedState;
    }

    isVisited() {
        return this.state === VisitedState;
    }

    isVisiting() {
        return this.state === VisistingState;
    }

    isSingleInDegree() {
        return this.inDegree <= 1;
    }

    isSingleOutDegree() {
        return this.outDegree <= 1;
    }
}
