# Code
```
/**
 * @param {string} colors
 * @param {number[][]} edges
 * @return {number}
 */
var largestPathValue = function(colors, edges) {
    if (!edges.length) return 1

    const adList = {}
    const inDegree = {}

    edges.forEach(edge => {
        const [start, end] = edge 
        if (!adList[start]) adList[start] = new Set()
        adList[start].add(end)
        if (!inDegree[end]) inDegree[end] = 0 
        if (!inDegree[start]) inDegree[start] = 0 
        inDegree[end] ++
    })

    const counts = new Array(colors.length)
        .fill(new Array(26).fill(0))
        .map(c => new Array(26).fill(0))

    const queue = Object.keys(inDegree).filter(k => !inDegree[k])

    let answer = 0 
    let seen = 0 

    while (queue.length) {
        const node = queue.shift()
        seen ++
        counts[node][colors[node].charCodeAt(0) - 'a'.charCodeAt(0)] ++
        answer = Math.max(answer, counts[node][colors[node].charCodeAt(0) - 'a'.charCodeAt(0)])

        if (!adList[node]) {
            continue
        }

        adList[node].forEach(neig => {
            for (let i = 0; i < 26; i++) {
                counts[neig][i] = Math.max(counts[neig][i], counts[node][i])
            }

            inDegree[neig]--
            if (inDegree[neig] == 0) {
                queue.push(neig)
            }
        })

    }

    return seen == colors.length ? answer : -1
};
