class Solution:
    def largestPathValue(self, colors: str, edges: List[List[int]]) -> int:
        out = -1
        graph = {}
        in_number = {}
        dp = {}
        removed_edges = 0

        for a, b in edges:
            graph[a] = graph.get(a, set())
            graph[a].add(b)
            graph[b] = graph.get(b, set())
            in_number[a] = in_number.get(a,0)
            in_number[b] = in_number.get(b,0) + 1

        # topological sort
        stack = deque()
        for node in graph.keys():
            if in_number[node] == 0:
                stack.append(node)
                dp[node] = {}
                dp[node][colors[node]] = 1
        
        while stack:
            node = stack.pop()
            dp_self = dp.get(node, {})
            for nei in list(graph[node]):
                color = colors[nei]
                dp[nei] = dp.get(nei, {color: 1})
                dp_nei = dp[nei]
                dp_nei[color] = dp_nei.get(color, 1)
                out = max(out, dp_nei[color])
                for c in dp_self:
                    dp_nei[c] = max(dp_self[c]+(1 if c == color else 0), dp_nei.get(c, 0))
                    out = max(out, dp_nei[c])

                graph[node].remove(nei)
                in_number[nei] -= 1
                removed_edges += 1
                if not in_number[nei]:
                    stack.append(nei)
                
            del dp[node]
        
        if removed_edges != len(edges):
            return -1

        return max(out, 1)

