int largestPathValue(string colors, vector<vector<int>>& edges) {
	//build adjacency list
    vector<vector<int>> adj_list(colors.size());
    for(const auto &edge: edges)
        adj_list[edge[0]].push_back(edge[1]);

	//check for cycle using simple bfs
    if(isCyclic(adj_list, colors.size()))
        return -1;


    vector<int> dp(colors.size(), -1);
	// f checks path which have max number of nodes with given 'color' param
	// checking to be done starting from node 'i' param
    function<int(char,int)> f = [&](char color, int i){
        if(dp[i] != -1)
            return dp[i];

        int same_color = colors[i] == color; 
        int maxi = same_color;
        for(int next_node: adj_list[i]){
            auto nxt_val = f(color, next_node);
            maxi = max(same_color + nxt_val, maxi);
        }
        return dp[i] = maxi;
    };

    int maxi = INT_MIN;
    unordered_set<char> possible_colors;
    for(const auto color: colors) possible_colors.insert(color);

    for(const auto color: possible_colors){
        for(auto &x: dp) x = -1;
        for(int i=0; i<colors.size(); ++i)
            maxi = max(maxi, f(color, i));
    }
    return maxi;
}
