Just realized the idea is basically the same, but we have to store every path information in each node instead.
The time & space complexity is exponential to the vertices but linear to the number of paths O(2^|V|).

'''
class Solution {
public:

    int colorfulPathValue(string colors, vector<vector<int>>& edges) {
        int n = colors.size();

        vector<vector<int>> adj (n);
        vector<int> indegree (n, 0);
        for (auto& e : edges) {
            adj[e[0]].push_back(e[1]);
            indegree[e[1]]++;
        }

        // For each node, store path information instead 
        vector<vector<unordered_set<char>>> path_colors;
        path_colors.resize(n);

        queue<int> q;
        for (int i=0; i<n; ++i) {
            if (indegree[i] == 0) {
                q.push(i);

                unordered_set<char> tmp_set;
                tmp_set.insert(colors[i]);
                path_colors[i].emplace_back(tmp_set);
            }
        }

        int ans = 0;
        while (!q.empty()) {
            int node = q.front();
            q.pop();

            // Find the most colorful one
            for (int i=0; i<path_colors[node].size(); ++i) {
                ans = max(ans, (int) path_colors[node][i].size());
            }

            for (auto nbr : adj[node]) {
                // Forward the information of parent
                for (int i=0; i<path_colors[node].size(); ++i) {
                    unordered_set<char> tmp_set (path_colors[node][i]);
                    tmp_set.insert(colors[nbr]);
                    path_colors[nbr].emplace_back(tmp_set);
                }

                --indegree[nbr];
                if (indegree[nbr] == 0) {
                    q.push(nbr);
                }
            }
        }

        return ans;
    }
};
