```
class Solution {
    public int[][] colorBorder(int[][] grid, int r0, int c0, int color) {
        List<int[]> border = new ArrayList<int[]>();
        dfs(grid, r0, c0, grid[r0][c0], border, new boolean[grid.length][grid[0].length]);

        for (int[] coord : border) {
            int i = coord[0], j = coord[1];
            grid[i][j] = color;
        }

        return grid;
    }

    private void dfs(int[][] grid, int i, int j, int targetColor, List<int[]> border, boolean[][] visited) {
        if (i < 0 || j < 0 || i >= grid.length || j >= grid[0].length || visited[i][j] || grid[i][j] != targetColor) return;

        visited[i][j] = true;
        if (isBorder(grid, i, j, targetColor)) { border.add(new int[]{i, j}); }

        dfs(grid, i+1, j, targetColor, border, visited);
        dfs(grid, i-1, j, targetColor, border, visited);
        dfs(grid, i, j+1, targetColor, border, visited);
        dfs(grid, i, j-1, targetColor, border, visited);
    }

    private boolean isBorder(int[][] grid, int i, int j, int targetColor) {
        if (i == 0 || j == 0 || i == grid.length - 1 || j == grid[0].length - 1) return true;
        return grid[i-1][j] != targetColor || grid[i+1][j] != targetColor || grid[i][j-1] != targetColor || grid[i][j+1] != targetColor;
    }
}
