Approach : Flip the colors by making it negative check if the given square satisfy the condition of boundary element or not, All the flipped squares will give connected components
```
class Solution {
    int color_;
    public boolean isValid(int i,int j,int row,int col){
        return i>=0 && i<row && j>=0 && j<col;
    }
    public boolean nonBoundary(int [][]grid,int i,int j,int color){
        return color == Math.abs(grid[i-1][j]) && color == Math.abs(grid[i][j-1]) && color == Math.abs(grid[i+1][j]) && color == Math.abs(grid[i][j+1]);
    }
    public void dfs(int [][]grid,int startcolor,int i,int j){
        int m = grid.length;
        int n = grid[0].length;
        if(!isValid(i,j,m,n) || grid[i][j]!=startcolor) return;
        grid[i][j] = -startcolor;
        dfs(grid,startcolor,i+1,j);
        dfs(grid,startcolor,i,j+1);
        dfs(grid,startcolor,i,j-1);
        dfs(grid,startcolor,i-1,j);
        if(i>0 && i<m-1 && j>0 && j<n-1){
            if(nonBoundary(grid,i,j,startcolor)){
                grid[i][j] = startcolor;
            }
        }

    }

    public int[][] colorBorder(int[][] grid, int r0, int c0, int color) {
        int startcolor = grid[r0][c0];
        color_ = color;
        dfs(grid,startcolor,r0,c0);
        for(int i=0;i<grid.length;i++){
            for(int j=0;j<grid[0].length;j++){
                if(grid[i][j]<0){
                    grid[i][j] = color;
                }
            }
        }
        return grid;
    }
}
