```class Solution {
    public int[][] colorBorder(int[][] grid, int r0, int c0, int color) {
        int m = grid.length;
        int n = grid[0].length;
        Stack<int[]> stack = new Stack<>();
        stack.push(new int[]{r0, c0});
        int origin = grid[r0][c0];
        boolean[][] visited = new boolean[m][n];
        while (!stack.isEmpty()) {
            int[] t = stack.pop();
            int r = t[0];
            int c = t[1];
            if (!visited[r][c]) {
                if (r == 0 || r == m-1 || c == 0 || c == n-1 || (grid[r-1][c] != origin && grid[r-1][c] != -1) || 
                   (grid[r+1][c] != origin && grid[r+1][c] != -1) || (grid[r][c-1] != origin && grid[r][c-1] != -1) || 
                    (grid[r][c+1] != origin && grid[r][c+1] != -1)) 
                    grid[r][c] = -1; 

                visited[r][c] = true;
                if (r - 1 >= 0 && origin == grid[r-1][c]) {
                    stack.push(new int[]{r-1, c});
                }

                if (r + 1 < m && origin == grid[r+1][c]) {
                    stack.push(new int[]{r+1, c});
                }

                if (c - 1 >= 0 && origin == grid[r][c-1]) {
                    stack.push(new int[]{r, c-1});
                }

                if (c + 1 < n && origin == grid[r][c+1]) {
                    stack.push(new int[]{r, c+1});
                }
            }
        }

        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (grid[i][j] == -1) {
                    grid[i][j] = color;
                }
            }
        }

        return grid;
    }
}

