class Solution {
    int dirx[4] = {0,1,0,-1};
    int diry[4] = {1,0,-1,0};
public:
    vector<vector<int>> colorBorder(vector<vector<int>>& grid, int r0, int c0, int color) {
        int m = grid.size(), n = grid[0].size();
        queue<pair<int, int>> q;
        vector<vector<int>> ans = grid;

        int sc = grid[r0][c0];
        q.push({r0,c0});
        grid[r0][c0] = 2000;

        while(q.size()) {
            pair<int, int> p = q.front(); q.pop();
            int x = p.first, y = p.second;
            bool isBoundary = false;

            for(int k=0; k<4; k++) {
                int nx = x + dirx[k];
                int ny = y + diry[k];

                if(nx==-1 or nx==m or ny==-1 or ny==n or (grid[nx][ny]!=2000 and grid[nx][ny]!=sc)) {
                    isBoundary = true;
                }

                if(nx>=0 and nx<m and ny>=0 and ny<n and grid[nx][ny] == sc) {
                    grid[nx][ny] = 2000;
                    q.push({nx, ny});
                }
            }

            if(isBoundary) ans[x][y] = color;
        }


        return ans;
    }
};

