    void dfs(vector<vector<int>>& grid, int r, int c,int initial_color)
    {


        // Get size of the gird
        int n=grid.size(),m=grid[0].size(),check=0;

        // Check if the given row and column are valid and if the element hasnt already been visited
        if(r>=n || r<0 || c>=m || c<0 || grid[r][c]!=initial_color) return;

        // Check if the given element is at the border or not(checking if it is adjacent to a square not in the connected component)
        if(r+1<n && (grid[r+1][c]!=initial_color && grid[r+1][c]!=1001 && grid[r+1][c]!=1002)) check=1;
        if(r-1>=0 && (grid[r-1][c]!=initial_color && grid[r-1][c]!=1001 && grid[r-1][c]!=1002)) check=1;
        if(c+1<m && (grid[r][c+1]!=initial_color && grid[r][c+1]!=1001 && grid[r][c+1]!=1002)) check=1;
        if(c-1>=0 && (grid[r][c-1]!=initial_color && grid[r][c-1]!=1001 && grid[r][c-1]!=1002)) check=1;

        // Checking if the element is on the boundry and hence a border element
        if(c==m-1 || c==0 || r==0 || r==n-1) check=1;

        // Marking it with 1001 if its a border element
        if(check)
            grid[r][c]=1001;

        // Marking it with 1002 if its a central element(non border)
        else
            grid[r][c]=1002;

        // checking and visiting all its immediate neighbours
        dfs(grid,r+1,c,initial_color);
        dfs(grid,r-1,c,initial_color);
        dfs(grid,r,c+1,initial_color);
        dfs(grid,r,c-1,initial_color);

        // Changing the central element to its original value hence marking it unvisited 
        if(grid[r][c]==1002) grid[r][c]=initial_color;
        return ;

    }
    vector<vector<int>> colorBorder(vector<vector<int>>& grid, int r0, int c0, int color) {
        int initial_color=grid[r0][c0];
        dfs(grid,r0,c0,initial_color);

        // Changing 1001 to the desired color 
        for(int i=0;i<grid.size();i++)
            for(int j=0;j<grid[0].size();j++)
                if(grid[i][j]==1001) grid[i][j]=color;         
        return grid;
    }
};
