Temporary visited array is made

			vis[i][j]=1     -->     point is visited before
			vis[i][j]=2     -->     point needs to be colored as given color
2 conditions to color a point:
1) If current point is boundary itself
2) if any point adjacent does not have the same color as connected component color
In both these cases... the current point will be coloured
```
class Solution {
public:
    int dx[4]={-1,0,1,0};
    int dy[4]={0,1,0,-1};
    void dfs(int u,int v,vector<vector<int>>& A,vector<vector<int>>& vis,int c){
        bool ans=false;// denotes weather the color of curent point needs to be changed
        vis[u][v]=1;
        int n=A.size();
        int m=A[0].size();
        for(int i=0;i<4;i++){
            int x=u+dx[i];
            int y=v+dy[i];
            if(x>=0 && x<=n-1 && y>=0 && y<=m-1 && !vis[x][y] && A[x][y]==c){
                dfs(x,y,A,vis,c);//If same color node(unvisited) is found then do the dfs.
            }
            if(x>=0 && x<=n-1 && y>=0 && y<=m-1 && A[x][y]!=c){
                ans=true;// If another color adjacent point is found then current point's color needs to be changed
            }
        }
        if(u==0 || v==0 || u==n-1 || v==m-1 || ans){
            vis[u][v]=2;
        }
    }
    vector<vector<int>> colorBorder(vector<vector<int>>& A, int r0, int c0, int color) {
        int n=A.size();
        int m=A[0].size();
        int c=A[r0][c0];
        vector<vector<int>> vis(n,vector<int>(m,0));
        dfs(r0,c0,A,vis,c);
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(vis[i][j]==2){
                    A[i][j]=color;
                }
            }
        }
        return A;
    }
};
