    public class Solution
    {
        private class Unions
        {
            private readonly int[] _parents;
            private readonly int[] _ranks;

            public Unions(int n)
            {
                _parents = new int[n];
                _ranks = new int[n];
                for (int i = 0; i < n; i++)
                {
                    _parents[i] = i;
                }
            }

            public int Find(int x)
            {
                if (x != _parents[x])
                {
                    x = Find(_parents[x]);
                }
                return _parents[x];
            }

            public bool Union(int x, int y)
            {
                int px = Find(x);
                int py = Find(y);
                if (px == py)
                {
                    return false;
                }
                if (_ranks[px] > _ranks[py])
                {
                    _parents[py] = px;
                    _ranks[px]++;
                }
                else
                {
                    _parents[px] = py;
                    _ranks[py]++;
                }
                return true;
            }
        }

        private static readonly (int, int)[] _directions = { (0,1), (0,-1), (1,0), (-1,0) };

        public int[][] ColorBorder(int[][] grid, int r0, int c0, int color)
        {
            int n = grid.Length;
            int m = grid[0].Length;

            Unions dsu = new Unions(n * m);

            for (int i = 0; i < n; i++)
            {
                for (int j = 0; j < m; j++)
                {
                    int ij = i * m + j;

                    foreach (var dir in _directions)
                    {
                        int newI = i + dir.Item1;
                        int newJ = j + dir.Item2;

                        if (newI >= 0 && newI < n && newJ >= 0 && newJ < m && grid[i][j] == grid[newI][newJ])
                        {
                            dsu.Union(ij, newI * m + newJ);
                        }
                    }
                }
            }

            var targetRoot = dsu.Find(r0 * m + c0);
            ISet<(int,int)> border = new HashSet<(int, int)>();

            for (int i = 0; i < n; i++)
            {
                for (int j = 0; j < m; j++)
                {
                    int ij = i * m + j;

                    if (dsu.Find(ij) != targetRoot)
                    {
                        continue;
                    }

                    foreach (var dir in _directions)
                    {
                        int newI = i + dir.Item1;
                        int newJ = j + dir.Item2;

                        if (newI < 0 || newI >= n || newJ < 0 || newJ >= m || dsu.Find(newI * m + newJ) != targetRoot)
                        {
                            border.Add((i, j));
                        }
                    }
                }
            }

            foreach (var cell in border)
            {
                grid[cell.Item1][cell.Item2] = color;
            }

            return grid;
        }
    }
