            ISet<(int,int)> border = new HashSet<(int, int)>();

            for (int i = 0; i < n; i++)
            {
                for (int j = 0; j < m; j++)
                {
                    int ij = i * m + j;

                    if (dsu.Find(ij) != targetRoot)
                    {
                        continue;
                    }

                    foreach (var dir in _directions)
                    {
                        int newI = i + dir.Item1;
                        int newJ = j + dir.Item2;

                        if (newI < 0 || newI >= n || newJ < 0 || newJ >= m || dsu.Find(newI * m + newJ) != targetRoot)
                        {
                            border.Add((i, j));
                        }
                    }
                }
            }

            foreach (var cell in border)
            {
                grid[cell.Item1][cell.Item2] = color;
            }

            return grid;
        }
    }
