    public int[][] colorBorder(int[][] grid, int r0, int c0, int color) {
        if (grid == null || grid.length == 0) return grid;
        int m = grid.length;
        int n = grid[0].length;
        boolean[][] visited = new boolean[m][n];
        visited[r0][c0] = true;
        dfs(grid, r0, c0, grid[r0][c0], color, visited);
        return grid;
