    private void dfs(int[][] grid, int r, int c, int targetColor, int color, boolean[][] visited) {
        int count = 0; // the number of neighbors is connected
        for (int[] dir : dirs) {
            int x = r + dir[0];
            int y = c + dir[1];
            if (x < 0 || y < 0 || x >= grid.length || y >= grid[0].length) continue;
            if (grid[x][y] == targetColor || visited[x][y]) count++; // If the neighbor is our target, Or if we've already visited it, which means its connected 
            if (grid[x][y] == targetColor && !visited[x][y]) { // We only visit the neighbor whose color is our target.
                visited[x][y] = true; 
                dfs(grid, x, y, targetColor, color, visited);
            }
        }

        if (count != 4) { // if the count < 4, which means its a border, we need to change the color
            grid[r][c] = color;
        }
        return;
