main idea is that if a node does not have similar values on all 4 sides than it is border cell. 

# Code
```
class Solution {
public:
    vector<vector<int>> colorBorder(vector<vector<int>>& grid, int row, int col, int color) {
        queue<pair<int,int>> q;
        q.push({row,col});
        int m=grid.size();
        int n=grid[0].size();
        int clr=grid[row][col];

        vector<vector<int>> dir = {{-1,0},{1,0},{0,1},{0,-1}};
        vector<vector<bool>> vi(m,vector<bool>(n,0));
        vi[row][col]=1;

        while(!q.empty()){
            int r=q.front().first;
            int c=q.front().second;
            q.pop();

            int cnt=0;
            for(int i=0;i<dir.size();i++){
                int nr=r+dir[i][0];
                int nc=c+dir[i][1];
                if(nr<m && nr>=0 && nc<n && nc>=0){
                    if(vi[nr][nc]==1) cnt++;
                    else if(grid[nr][nc]==clr){
                        cnt++;
                        vi[nr][nc]=1;
                        q.push({nr,nc});
                    }
                }
            }
            if(cnt!=4) grid[r][c]=color;
        }
        return grid;
    }
};
