				int originalColor = grid[row][col];

				visited = vector<vector<bool>>(grid.size(),vector<bool>(grid[0].size(),0));
				queue<Pair> qu;
				qu.push(Pair(row,col, isBorder(grid,row,col)));

				vector<Pair> vp;
				while(!qu.empty()){
					auto top = qu.front();
