class Solution {
   public int[][] colorBorder(int[][] grid, int row, int col, int color) {
        if (grid == null || grid.length == 0) {
            return grid;
        }
        int val = grid[row][col];
        boolean[][] visited = new boolean[grid.length][grid[0].length];
        dfs(grid, row, col, val, visited);
        fillGrid(grid, color, visited);
        return grid;
    }

    public void dfs(int[][] grid, int row, int col, int val, boolean[][] visited) {

        if (row >= grid.length || col >= grid[0].length || row < 0 || col < 0 || grid[row][col] != val || visited[row][col]) {
            return;
        }

        //LURD
        if (grid[row][col] == val) {
            visited[row][col] = true;
            dfs(grid, row, col - 1, val, visited);
            dfs(grid, row + 1, col, val, visited);
            dfs(grid, row, col + 1, val, visited);
            dfs(grid, row - 1, col, val, visited);
        }
    }

    public void fillGrid(int[][] grid, int color, boolean[][] visited) {

        for (int i = 0; i < grid.length; i++) {
            for (int j = 0; j < grid[0].length; j++) {

                if (visited[i][j]) {

                    //color boundary elements
                    if (i + 1 >= grid.length || j + 1 >= grid[0].length || i - 1 < 0 || j - 1 < 0) {
                        grid[i][j] = color;
                    }
                    //middle element
                    else if ((visited[i + 1][j] && visited[i - 1][j] && visited[i][j - 1] && visited[i][j + 1])) {
                        continue;
                    }
                    else{
                        grid[i][j] = color;
                    }
                }
            }
        }
    }



