class Solution {
    public int[][] colorBorder(int[][] grid, int row, int col, int color) {
        find(grid, row, col, grid[row][col]);
        for(int i = 0; i < grid.length; i++){
            for(int j = 0; j < grid[0].length; j++){
                if(grid[i][j] < 0)
                    grid[i][j] = color;
            }
        }
        return grid;
    }

    public int[][] dir = {{1, 0}, {0, 1}, {-1, 0}, {0, -1}};
    public void find(int[][] grid, int r, int c, int toChange){
        grid[r][c] = -toChange;
        int count = 0;       
        for(int i = 0; i < 4; i++){
            int nr = r + dir[i][0];
            int nc = c + dir[i][1];         
            if(nr < 0 || nr == grid.length || nc < 0 || nc == grid[0].length || Math.abs(grid[nr][nc]) != toChange)
                continue;
            count++;
            if(grid[nr][nc] == toChange)
                find(grid, nr, nc, toChange);
        }        
        if(count == 4)
            grid[r][c] = toChange;
    }
}
