        int t = que.front();
        que.pop();

        int r = t/m;
        int c = t%m;

       if (r == 0 || r == n - 1 || c == 0 || c == m - 1) { grid[r][c] = color; }


        for(int d=0;d<4;d++){
            int nr= r + dir[d][0];
            int nc= c + dir[d][1];
