/**
 * @param {number[][]} grid
 * @param {number} r0
 * @param {number} c0
 * @param {number} color
 * @return {number[][]}
 */
const colorBorder = function(grid, r0, c0, color) {
   if (grid[r0][c0] === color) { return grid; }

    const visited = new Array(grid.length).fill(0).map( row => {
        return new Array(grid[0].length).fill(false);
    })

    dfs(grid, visited, grid[r0][c0], r0, c0, color);

    return grid;
};

const dfs = function(grid, visited, prevVal, r, c, color){
    /*  Order here matters  */
    if ( r < 0 || c < 0 || r === grid.length ||  c === grid[0].length) { return -1; }

    if (visited[r][c]) { return color; }

    if(grid[r][c] !== prevVal) { return -1; }


    visited[r][c] = true;
    const v1 = dfs(grid, visited, grid[r][c], r+1, c, color);
    const v2 = dfs(grid, visited, grid[r][c], r-1, c, color);
    const v3 = dfs(grid, visited, grid[r][c], r, c+1, color);
    const v4 = dfs(grid, visited, grid[r][c], r, c-1, color);

    if( ![v1, v2, v3, v4].every( val => ( val === color || val === prevVal))){
        grid[r][c] = color;
    }

    return grid[r][c];
}
