        return grid;
    }

    private void dfs(int[][] grid, int i, int j, int m, int n, int color, int ic, boolean[][] visited) {

        if (i < 0 || j < 0 || i == m || j == n || visited[i][j] || grid[i][j] != ic) return;

        if (changeColor(grid, i, j, m, n, ic, visited)) {
            grid[i][j] = color;    
        } 

        visited[i][j] = true;

        for (int[] d : dir) {
            int x = i + d[0];
            int y = j + d[1];

            dfs(grid, x, y, m, n, color, ic, visited);
        }
    }

    private boolean changeColor(int[][] grid, int i, int j, int m, int n, int ic, boolean[][] visited) {

        if (i == 0 || j == 0 || i+1 == m || j+1 == n) return true;

        int borders = 4;
        for (int[] d : dir) {
            int x = i + d[0];
            int y = j + d[1];

            if (visited[x][y] || grid[x][y] == ic) {
                borders--;    
            }
        }

        return borders > 0;
    }
}
