class Solution {
public:
    vector<vector<int>> colorBorder(vector<vector<int>>& grid, int r0, int c0, int color) {
        vector<pair<int,int>> retv;
        queue<pair<int,int>> q;
        q.push(pair<int,int>(r0,c0));
        vector<vector<bool>> go(grid.size(),vector<bool>(grid[0].size(),false));
        vector<vector<int>> dirs = {{0,1},{0,-1},{1,0},{-1,0}};
        go[r0][c0] = true;
        while(!q.empty())
        {
            auto e = q.front();q.pop();
            int x = e.first;
            int y = e.second;
            for(auto d : dirs)
            {
                int _x = x+d[0];
                int _y = y+d[1];
                if(_x>=0&&_x<grid.size()&&_y>=0&&_y<grid[0].size()&&grid[_x][_y] == grid[x][y])
                {
                    if(go[_x][_y] == false)
                    {
                        q.push(pair<int,int>(_x,_y));    
                        go[_x][_y] = true;
                    }
                }
                else
                {
                    retv.push_back(pair<int,int>(x,y));
                }
            }
        }
        for(auto e: retv)
        {
            grid[e.first][e.second] = color;
        }
        return grid;
    }
};
