class Solution {
    public int[][] colorBorder(int[][] grid, int r0, int c0, int color) {
        int row = grid.length;
        int col = grid[0].length;
        boolean[][] visited = new boolean[row][col];
        boolean[][] border = new boolean[row][col];

        Queue<int[]> queue = new LinkedList<>();
        queue.add(new int[]{r0,c0});
        visited[r0][c0] = true; // visited

        int[][] steps = {{-1,0},{1,0},{0,1},{0,-1}};
        //System.out.println("now to bfs");

        while(!queue.isEmpty()) {
            int[] item = queue.poll();
            int x = item[0];
            int y = item[1];
            //System.out.println("x=" + x + ", y=" + y);
            for(int i = 0; i < steps.length; ++i) {
                int nextx = x + steps[i][0];
                int nexty = y + steps[i][1];
                if(nextx < 0 || nextx >= row || nexty < 0 || nexty >= col || grid[nextx][nexty] != grid[x][y]) {
                    border[x][y] = true; // is border
                }
                else { // so here, next color is the same 
                    if(!visited[nextx][nexty]) { // not visited
                        queue.add(new int[]{nextx, nexty});
                        visited[nextx][nexty] = true;
                    }
                }
            }
        }

        for(int i = 0; i < grid.length; ++i) {
            for(int j = 0; j < grid[i].length; ++j) {
                if(border[i][j]) {
                    grid[i][j] = color;
                }
            }
        }
        return grid;
    }
}
