               if((i-1>=0&&p[{i-1,j}]==0) || (i+1<=g.size()&&p[{i+1,j}]==0) || (j-1>=0&&p[{i,j-1}]==0) || (j+1<=g[0].size()&&p[{i,j+1}]==0))
               g[i][j]=col;

               if(i==g.size()-1 || j==g[0].size()-1 || i==0 || j==0)
               g[i][j]=col;
           }
       }
       return g;
    }
};
