class Solution {
public:
    void dfs(vector<vector<int>>& grid,int i,int j,int curColor,vector<vector<bool>>& visited)
    {
        if(i<0 or i==grid.size() or j<0 or j==grid[0].size() or grid[i][j]!=curColor or visited[i][j])
            return;

        visited[i][j]=true;
        grid[i][j]= -curColor;
        dfs(grid,i+1,j,curColor,visited);
        dfs(grid,i-1,j,curColor,visited);
        dfs(grid,i,j-1,curColor,visited);
        dfs(grid,i,j+1,curColor,visited);
        if(i>0 and i<grid.size()-1 and j>0 and j<grid[0].size()-1 and abs(grid[i+1][j])==curColor and abs(grid[i-1][j])==curColor and abs(grid[i][j-1])==curColor and abs(grid[i][j+1])==curColor)
            grid[i][j]=curColor;
    }

    vector<vector<int>> colorBorder(vector<vector<int>>& grid, int row, int col, int color) {
        vector<vector<bool>> visited(grid.size(),vector<bool>(grid[0].size(),false));
        dfs(grid,row,col,grid[row][col],visited);
        for(int i=0;i<grid.size();i++)
        {
            for(int j=0;j<grid[0].size();j++)
            {
                if(grid[i][j]<0)
                    grid[i][j]=color;
            }
        }
        return grid;
    }
};
