    int[][] dir = new int[][] {new int[]{0,1}, new int[]{0,-1}, new int[]{1, 0}, new int[]{-1, 0}};
    int m;
    int n;
    int toColo;
    int[][] visited;
    public int[][] ColorBorder(int[][] grid, int row, int col, int color) {
        m = grid.Length;
        n = grid[0].Length;
        visited = new int[m][];
        toColo = color;

        for(var r=0; r<m; ++r){
            visited[r] = new int[n];
            for(var c=0; c<n; ++c){
                visited[r][c] = -1;
            }
        }

        DFS(row, col, grid[row][col], grid);

        for(int i=0; i<m; i++)
        for(int j=0; j<n; j++){
            if(visited[i][j]==-1){
                visited[i][j] = grid[i][j];
            }
        }

        return visited;
    }

    public void DFS(int row, int col, int color, int[][] grid){
        if(visited[row][col]!=-1) return;

        visited[row][col] = color;

        if(row==0 || row==m-1 || col==0 || col==n-1){
            visited[row][col] = toColo;
        }

        foreach(var coor in dir){
            int nx = row + coor[0];
            int ny = col + coor[1];

            if(nx>=0 && nx<m && ny>=0 && ny<n){
                if(grid[nx][ny]==color){
                    DFS(nx, ny, color, grid);
                }
                else if(grid[row][col]!=toColo){
                    visited[row][col] = toColo;
                }
            }
        }
    }
}
