class Solution {
public:
    int dx[4]={-1,1,0,0};
    int dy[4]={0,0,1,-1};

    bool is_valid(vector<vector<int>> &grid,int r,int c){
        if(r<0 || c<0 || r>=grid.size() || c>=grid[0].size()){
            return false;
        }
        return true;
    }
    bool is_border(vector<vector<int>> grid,int r,int c){
        int rows=grid.size();
        int cols=grid[0].size();
        int og=grid[r][c];
        if(r==0 || r==rows-1 || c==0 || c==cols-1){
            return true;
        }

        if(grid[r+1][c]!=og || grid[r-1][c]!=og || grid[r][c-1]!=og || grid[r][c+1]!=og){
            return true;
        }
        return false;

    }

    vector<vector<int>> colorBorder(vector<vector<int>>& grid, int row, int col, int color) {
        int rows=grid.size();
        int cols=grid[0].size();
        queue<pair<int,int>> q;
        q.push({row,col});
        vector<vector<int>> ans=grid;
        vector<vector<int>> visited(rows,vector<int>(cols,-1));
        visited[row][col]=1;
        int og=grid[row][col];
        while(!q.empty()){
            int size=q.size();

            for(int i=0;i<size;i++){
                auto fro=q.front();
                q.pop();
                int r=fro.first;
                int c=fro.second;

                if(is_border(grid,r,c)){
                    ans[r][c]=color;
                }

                for(int j=0;j<4;j++){
                    int nr=r+dx[j];
                    int nc=c+dy[j];

if(is_valid(grid,nr,nc) && visited[nr][nc]==-1 && grid[nr][nc]==og){
                visited[nr][nc]=1;
                q.push({nr,nc});
                }

                }
            }

        }
        return ans;

    }
};
